import React from 'react';
import HelloWorld from './HelloWorld';
import GreetingCard from './GreetingCard';

function App() {
  return (
    <div style={{ padding: '20px', backgroundColor: '#f5f5f5', minHeight: '100vh' }}>
      {/* Bài 1: HelloWorld Component */}
      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ textAlign: 'center', color: '#333', marginBottom: '20px' }}>
          Bài 1: Hello World Component
        </h2>
        <HelloWorld />
      </div>

      <hr style={{ margin: '40px 0', border: '1px solid #ddd' }} />

      {/* Bài 2: GreetingCard Component */}
      <div>
        <h2 style={{ textAlign: 'center', color: '#333', marginBottom: '20px' }}>
          Bài 2: Greeting Card Component
        </h2>
        <div style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          gap: '10px'
        }}>
          <GreetingCard name="Alice" />
          <GreetingCard name="Bob" />
          <GreetingCard name="Charlie" />
          <GreetingCard name="Diana" />
        </div>
      </div>
    </div>
  );
}

export default App;
