import React, { useState } from 'react';

// B√†i 4: ToggleVisibility Component
function ToggleVisibility() {
  // useState Hook ƒë·ªÉ qu·∫£n l√Ω boolean state cho visibility (kh·ªüi t·∫°o = false)
  const [isVisible, setIsVisible] = useState(false);

  // Event handler ƒë·ªÉ toggle visibility
  const toggleVisibility = () => {
    setIsVisible(!isVisible);
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: '30px',
      backgroundColor: '#fff',
      borderRadius: '15px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      maxWidth: '500px',
      margin: '0 auto'
    }}>
      {/* Toggle Button */}
      <button 
        onClick={toggleVisibility}
        style={{
          padding: '15px 30px',
          fontSize: '18px',
          backgroundColor: isVisible ? '#dc3545' : '#28a745',
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          cursor: 'pointer',
          fontWeight: 'bold',
          transition: 'all 0.3s ease',
          marginBottom: '30px'
        }}
        onMouseOver={(e) => {
          e.target.style.backgroundColor = isVisible ? '#c82333' : '#218838';
        }}
        onMouseOut={(e) => {
          e.target.style.backgroundColor = isVisible ? '#dc3545' : '#28a745';
        }}
      >
        {isVisible ? 'Hide Content' : 'Show Content'}
      </button>

      {/* Conditional Rendering - Content ch·ªâ hi·ªÉn th·ªã khi isVisible = true */}
      {isVisible && (
        <div style={{
          padding: '20px',
          backgroundColor: '#f8f9fa',
          borderRadius: '10px',
          border: '2px solid #dee2e6',
          textAlign: 'center',
          animation: 'fadeIn 0.3s ease-in'
        }}>
          <h3 style={{ 
            color: '#495057', 
            marginBottom: '15px',
            fontSize: '1.5rem'
          }}>
            üéâ N·ªôi dung ƒë∆∞·ª£c hi·ªÉn th·ªã!
          </h3>
          <p style={{ 
            color: '#6c757d', 
            fontSize: '16px',
            lineHeight: '1.5',
            margin: 0
          }}>
            ƒê√¢y l√† ƒëo·∫°n vƒÉn b·∫£n m·∫´u s·ª≠ d·ª•ng conditional rendering trong React. 
            Khi b·∫°n click button "Hide Content", ƒëo·∫°n vƒÉn n√†y s·∫Ω bi·∫øn m·∫•t. 
            ƒê√¢y l√† m·ªôt v√≠ d·ª• tuy·ªát v·ªùi v·ªÅ c√°ch s·ª≠ d·ª•ng useState Hook v√† 
            to√°n t·ª≠ logical && ƒë·ªÉ hi·ªÉn th·ªã/·∫©n n·ªôi dung.
          </p>
        </div>
      )}

      {/* Animation CSS */}
      <style jsx>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
}

export default ToggleVisibility;
