import React from 'react';

function Home() {
  return (
    <div style={{
      textAlign: 'center',
      padding: '50px 20px',
      backgroundColor: '#f8f9fa',
      borderRadius: '10px',
      margin: '20px 0'
    }}>
      <h1 style={{ 
        color: '#007bff', 
        marginBottom: '20px',
        fontSize: '3rem'
      }}>
        üè† Welcome Home
      </h1>
      <p style={{ 
        color: '#6c757d', 
        fontSize: '18px',
        lineHeight: '1.6',
        maxWidth: '600px',
        margin: '0 auto'
      }}>
        This is the home page of our React Router application. 
        Navigate through different pages using the menu above to explore 
        the routing functionality.
      </p>
      <div style={{
        marginTop: '30px',
        padding: '20px',
        backgroundColor: '#e3f2fd',
        borderRadius: '8px',
        display: 'inline-block'
      }}>
        <p style={{ 
          color: '#1976d2', 
          margin: 0,
          fontWeight: 'bold'
        }}>
          ‚ú® React Router Demo
        </p>
        <p style={{ 
          color: '#1976d2', 
          margin: '5px 0 0 0',
          fontSize: '14px'
        }}>
          Single Page Application with Client-side Routing
        </p>
      </div>
    </div>
  );
}

export default Home;
